INSERT INTO `memodb` (`idx`, `system_gubun`, `subject`, `memo`, `description`, `reg_date`, `last_update_date`, `flag`) VALUES
(1, 1, '테스트', '*1 단지정보 전송\r\n*2 동/호 정보 전송\r\n*3 매입정보\r\n*4 입주민정보수신\r\n*5 매입결과정보 수신\r\n\r\nzet = 트랙잭션(거래를 하면서 카드사 승인등 )\r\nzem = 기준정보\r\nzeh = 히스토리(로그성)\r\nzec = 코드성 테이블\r\nzes = 통계용 테이블들\r\n\r\nzem_complex_info = apt 단지정보\r\nzem_building , house = 동호정보\r\nzem_applicont  = 입주민 정보\r\n\r\n\r\n   마감일        대상단지\r\n1. 23일 ----->  28 , 27 , 26  단지 찾기\r\n2. 단지 입주민 신규 / 기존\r\n3. zet_maint_fee_req 금액\r\n4. insert zet_edi_send\r\n5. 파일 req_dt 가 오늘이고 카드사 구분 finance_cp 00001 ~ 00006\r\n\r\n응답은 zet_edi_return\r\n', NULL, '20140430', NULL, NULL),
(3, 1, '대체적인 업무', '과장님, 아직 제대로 파악되지 않은 상태에서 이것저것 정신없으시죠.\r\n\r\n곧 적응 되실껍니다. ^^\r\n\r\n매월말 주로 들어오는 요청들입니다.\r\n이런요청들 페이지로 만들어두면 요청이 없겠죠?\r\n업무 파악이 좀 되시면 이런부분에대한 근본적인 개선이 필요할듯 합니다.\r\n\r\n정신없이 수작업/현업 요청 처리를 하다보니\r\n좀 정리되지 않는듯 해서 아래 정리를 해 두었으니\r\n이미 처리한것들이라도 신경써서 확인 부탁드립니다.\r\n\r\n1. 일일 모니터링\r\n - 매일 파일 들락날락하는것들이 잘 되고 있는지 정확히 파악되어야 합니다. 무척 중요하거든요.\r\n   가끔 송수신 오류로 뻑이나며\r\n   모듈상의 버그로 문제를 많이 일으켰던놈들은 신한쪽이 많습니다. (참고하세요)\r\n - 관련해서 카드사별 매입 요청및 결과에대한 테이블 작성 완료 부탁드려요.\r\n\r\n2. 월말 주로 들어오는 수작업\r\n 2-1. 결제 상태 변경\r\n   - 현업에서 카드사로 직접 취소하거나 환불 후 취소상태로 요청주는것들에 대한  query 를 수기로 작성중에 있습니다.\r\n 2-2. 거래 취소 요청\r\n   - 우리쪽에서 거래 취소를 하는경우 승인번호등의 데이터 확인이 필요하기때문에 수기로 처리하고 있습니다.\r\n\r\n3. 4/5일자 입주민 승인 지연 처리\r\n  - 4/5일 마감인 단지의 입주민의 경우 당월 승인을 위해\r\n    자동 승인일이 전월인경우 해당월에는 금액을 0원으로 당월 제일 첫 영업일에 승인을 낼 수 있도록 수기처리하고 있습니다.\r\n  - 해당 승인 결과를 원장에 update 하는 쿼리도 수기처리하고 있습니다.\r\n\r\n3. 벤피 추출\r\n  - 매월말 밴피 추출을 위해 query를 구동하고 있습니다. (이건 뭐 요대로 하면 될듯 해요)\r\n\r\n\r\n', NULL, '20140502', NULL, NULL),
(4, 1, '일별 전송쿼리.', 'SELECT za.complex_cd, za.bd_cd, za.house_no, \\''X\\'', charge_ym, to_char(sysdate+1,\\''YYYYMMDD\\'') , crd_owner, za.finance_cp, finance_cp_type, \r\n                SEQ_EDI_REQ.NEXTVAL , \\''\\'', crd_no, expdt, \\''000\\'', to_char(sysdate+1,\\''YYYYMMDD\\'') , \\''\\'', \\''\\'', \\''410\\'', \\''0\\'', tmm_pay_amt, \r\n                \\''0\\'', tmm_pay_amt, \\''00\\'', to_char(add_months(sysdate+1,-1),\\''YYYYMM\\''), \\''00\\'', \\''\\'', \\''00\\'', \\''\\'', \\''2\\'', short_complex_nm, \r\n                zmf.print_bd, \\''EDI04\\'', to_char(sysdate+1,\\''YYYYMMDD\\''), to_char(sysdate+1,\\''YYYYMMDD\\''), to_char(sysdate+1,\\''YYYYMMDD\\''),\\''\\'', \\''\\'', \\''\\'', \r\n                payment.fn_bizday(to_char(sysdate+1,\\''YYYYMMDD\\''), 3) ,\r\n                sysdate+1, \\''\\'', \\''\\'' FROM zem_applicant@zeusreallink za, zem_complex_info@zeusreallink ci,  (\r\n                    select targetday from (\r\n						SELECT CASE WHEN ( ( SELECT max(targetday) - min(targetday) FROM ( SELECT payment.fn_bizday(targetday, - 3) AS today, to_number(substr(targetday, 7,2)) as targetday FROM\r\n						        ( select to_char(sysdate+1 + no -15, \\''YYYYMMDD\\'') targetday from ( select level no from dual connect by level <= 31) ) ) c WHERE c.today = to_char(sysdate+1,\\''YYYYMMDD\\'')  )  > 20)\r\n						    THEN ( select case when (SELECT to_number(TO_CHAR(LAST_DAY(sysdate+1),\\''DD\\'')) FROM DUAL) < 29 then 29 end as targetday from dual ) end as targetday from dual\r\n						union\r\n						SELECT CASE WHEN ( (SELECT max(targetday) - min(targetday) FROM ( SELECT payment.fn_bizday(targetday, - 3) AS today, to_number(substr(targetday, 7,2)) as targetday FROM\r\n						        ( select to_char(sysdate+1 + no -15, \\''YYYYMMDD\\'') targetday from ( select level no from dual connect by level <= 31) ) ) c WHERE c.today = to_char(sysdate+1,\\''YYYYMMDD\\'')  )  > 20)\r\n						    THEN ( select case when (SELECT to_number(TO_CHAR(LAST_DAY(sysdate+1),\\''DD\\'')) FROM DUAL) < 30 then 30 end as targetday from dual ) end as targetday from dual\r\n						union\r\n						SELECT CASE WHEN ( ( SELECT max(targetday) - min(targetday) FROM ( SELECT payment.fn_bizday(targetday, - 3) AS today, to_number(substr(targetday, 7,2)) as targetday FROM\r\n						        ( select to_char(sysdate+1 + no -15, \\''YYYYMMDD\\'') targetday from ( select level no from dual connect by level <= 31) ) ) c WHERE c.today = to_char(sysdate+1,\\''YYYYMMDD\\'')  )  > 20)\r\n						    THEN ( select case when (SELECT to_number(TO_CHAR(LAST_DAY(sysdate+1),\\''DD\\'')) FROM DUAL) < 31 then 31 end as targetday from dual ) end as targetday from dual\r\n						union\r\n			            SELECT CASE WHEN payment.fn_bizday(to_char(sysdate+1,\\''YYYYMMDD\\''), 3) = TO_CHAR(LAST_DAY(sysdate+1),\\''YYYYMMDD\\'') \r\n			                         AND TO_CHAR(LAST_DAY(sysdate+1),\\''DD\\'') in (\\''28\\'') THEN 29\r\n			                    END as targetday\r\n			              from dual\r\n			            union\r\n			            SELECT CASE WHEN payment.fn_bizday(to_char(sysdate+1,\\''YYYYMMDD\\''), 3) = TO_CHAR(LAST_DAY(sysdate+1),\\''YYYYMMDD\\'') \r\n			                         AND TO_CHAR(LAST_DAY(sysdate+1),\\''DD\\'') in (\\''28\\'',\\''29\\'') THEN 30\r\n			                    END as targetday\r\n			              from dual\r\n			            union\r\n			            SELECT CASE WHEN payment.fn_bizday(to_char(sysdate+1,\\''YYYYMMDD\\''), 3) = TO_CHAR(LAST_DAY(sysdate+1),\\''YYYYMMDD\\'') \r\n			                         AND TO_CHAR(LAST_DAY(sysdate+1),\\''DD\\'') in (\\''28\\'',\\''29\\'',\\''30\\'') THEN 31\r\n			                    END as targetday\r\n			              from dual\r\n			            union \r\n						SELECT targetday FROM (\r\n						    SELECT payment.fn_bizday(targetday, - 3) AS today, to_number(substr(targetday, 7,2)) as targetday FROM\r\n						        ( select to_char(sysdate+1 + no -15, \\''YYYYMMDD\\'') targetday from ( select level no from dual connect by level <= 31) )\r\n						    ) c\r\n						WHERE c.today = to_char(sysdate+1,\\''YYYYMMDD\\'')\r\n                    ) cc\r\n                    where cc.targetday is not null\r\n        ) aa ,zet_maint_fee_req@zeusreallink zmf\r\n        WHERE due_dt in aa.targetday\r\n        AND za.complex_cd = ci.complex_cd  \r\n        AND pay_start_dt <= payment.fn_bizday(to_char(sysdate+1,\\''YYYYMMDD\\''), 3 )\r\n        AND za.st_type <>3\r\n        AND ci.reg_proc_stat = \\''RG003\\''\r\n        AND za.complex_cd = zmf.complex_cd \r\n        AND za.bd_cd = zmf.bd_cd \r\n        AND za.house_no = zmf.house_no \r\n        AND charge_ym = (SELECT CASE WHEN \r\n                        	3 <= TO_CHAR(LAST_DAY(sysdate+1), \\''dd\\'') - TO_CHAR(sysdate+1, \\''dd\\'') \r\n                            THEN TO_CHAR(ADD_MONTHS(sysdate+1, -1), \\''YYYYMM\\'') \r\n                            ELSE TO_CHAR(sysdate+1, \\''YYYYMM\\'') END FROM DUAL ) \r\n		AND za.finance_cp = \\''00001\\''\r\n		AND (za.card_cl is null or za.card_cl = \\''0\\'')\r\n\r\n\r\n\r\n		    \r\n		    \r\n		    \r\n		    \r\n		    select * from zet_maint_fee_req@zeusreallink \r\n                    where complex_cd =\\''N0169\\''\r\n                    and bd_cd =\\''0004\\''\r\n                    and house_no =\\''0015\\''\r\n                    order by charge_ym desc', NULL, '20140502', NULL, NULL),
