(20, 1, '자동이체 등록현황 데이터 추출 (엑셀)', 'SELECT * FROM (\r\n   SELECT ROWNUM RN, A.* FROM (\r\n   \r\n	SELECT ROW_NUMBER() OVER(ORDER BY B.BD_NM, H.HOUSE_NO_NM) GUBUN\r\n	      ,C.COMPLEX_CD\r\n	      ,C.COMPLEX_NM\r\n	      ,CODE.CD_NM\r\n	      ,F.FINANCE_CD_NM \r\n	      ,B.BD_NM\r\n	      ,H.HOUSE_NO_NM\r\n	      ,A.APLIT_DT\r\n	      ,R.WITHIN_SUM_AMT\r\n	      ,TO_CHAR(ADD_MONTHS(TO_DATE(\\''201403\\'', \\''YYYYMM\\''), 1), \\''YYYYMM\\'') || C.DUE_DT AS DUE_DT\r\n	      ,A.TELNO\r\n	      ,\\''201403\\'' AS CHARGE_YM\r\n	  FROM ZET_MAINT_FEE_REQ R\r\n	      ,ZEM_APPLICANT     A\r\n	      ,ZEM_COMPLEX_INFO  C\r\n	      ,ZEM_BUILDING      B\r\n	      ,ZEM_HOUSE         H\r\n	      ,(SELECT * FROM ZEC_CODE C WHERE C.GRP_CD = \\''AA007\\'') CODE\r\n	      ,ZEM_FINANCE_COMPANY F\r\n	 WHERE A.COMPLEX_CD = \\''N0018\\''\r\n	   AND  A.SETTL_YM <= TO_CHAR(ADD_MONTHS(TO_DATE(\\''201403\\'', \\''YYYYMM\\''), 1), \\''YYYYMM\\'') \r\n	   AND  NVL(A.CLS_DT, \\''29001231\\'') > \\''201403\\'' || C.DUE_DT \r\n	   AND A.COMPLEX_CD = R.COMPLEX_CD(+)\r\n       AND A.BD_CD      = R.BD_CD(+)\r\n       AND A.HOUSE_NO   = R.HOUSE_NO(+)\r\n       AND A.DTAIL_DIV  = R.DTAIL_DIV(+)\r\n       AND R.CHARGE_YM(+) = \\''201403\\''\r\n       AND A.COMPLEX_CD = C.COMPLEX_CD\r\n       AND A.COMPLEX_CD = B.COMPLEX_CD\r\n       AND A.BD_CD      = B.BD_CD\r\n       AND A.COMPLEX_CD = H.COMPLEX_CD\r\n       AND A.BD_CD      = H.BD_CD\r\n       AND A.HOUSE_NO   = H.HOUSE_NO\r\n       AND CODE.CMN_CD = A.PAY_TYPE\r\n	   AND F.finance_cp = A.finance_cp and F.finance_cp_type = a.finance_cp_type\r\n	   AND NVL(A.CLS_DT, \\''29991231\\'') > TO_CHAR(ADD_MONTHS(TO_DATE(\\''201403\\'', \\''YYYY-MM\\''), 1), \\''YYYYMM\\'') || \\''31\\'' \r\n	 ORDER BY B.BD_NM, H.HOUSE_NO_NM   ) A\r\n   )', NULL, '20140520', NULL, NULL);
INSERT INTO `memodb` (`idx`, `system_gubun`, `subject`, `memo`, `description`, `reg_date`, `last_update_date`, `flag`) VALUES
(21, 1, '자동이체 등록현황 데이터 (단지별)', '	SELECT ROW_NUMBER() OVER(ORDER BY C.COMPLEX_CD) GUBUN\r\n	      ,C.COMPLEX_CD, C.COMPLEX_NM\r\n	      ,NVL(MAX(C.APT_HOUSE_CNT), 0) AS APT_HOUSE_CNT\r\n	      ,NVL(COUNT(A.COMPLEX_CD), 0) AS REQ_CNT\r\n	      ,NVL(COUNT(R.COMPLEX_CD), 0) AS UPLOAD_CNT\r\n	      ,NVL(SUM(R.WITHIN_SUM_AMT), 0) AS SUM_AMT\r\n	      , NVL( (select\r\n          count (a1.complex_cd)\r\n          from \r\n          zem_applicant a1,ZET_MAINT_FEE_REQ r1,ZEM_COMPLEX_INFO  C1\r\n          where r1.charge_ym = \\''201403\\''\r\n          and a1.complex_cd = r1.complex_cd(+)\r\n          and a1.bd_cd = r1.bd_cd(+)\r\n          and a1.house_no = r1.house_no(+)\r\n          AND A1.DTAIL_DIV  = R1.DTAIL_DIV(+)\r\n          and a1.complex_cd = c.complex_cd\r\n          and c1.complex_cd = a1.complex_cd\r\n          and C1.reg_proc_stat = \\''RG003\\''\r\n           AND NVL(A1.CLS_DT, \\''29001231\\'') > \\''201204\\'' || \\''31\\''\r\n           AND A1.DTAIL_DIV  = R1.DTAIL_DIV(+)\r\n           and A1.SETTL_YM <= TO_CHAR(ADD_MONTHS(TO_DATE(\\''201403\\'', \\''YYYYMM\\''), 1), \\''YYYYMM\\'') \r\n            and a1.finance_cp = \\''00001\\''\r\n        ), 0) AS  SHINHAN_CNT\r\n       	 , NVL( (select\r\n          count (a1.complex_cd)\r\n          from \r\n          zem_applicant a1,ZET_MAINT_FEE_REQ r1,ZEM_COMPLEX_INFO  C1\r\n          where r1.charge_ym = \\''201403\\''\r\n          and a1.complex_cd = r1.complex_cd(+)\r\n          and a1.bd_cd = r1.bd_cd(+)\r\n          and a1.house_no = r1.house_no(+)\r\n          AND A1.DTAIL_DIV  = R1.DTAIL_DIV(+)\r\n          and a1.complex_cd = c.complex_cd\r\n          and c1.complex_cd = a1.complex_cd\r\n          and C1.reg_proc_stat = \\''RG003\\''\r\n           AND NVL(A1.CLS_DT, \\''29001231\\'') > \\''201204\\'' || \\''31\\''\r\n           AND A1.DTAIL_DIV  = R1.DTAIL_DIV(+)\r\n          and A1.SETTL_YM <= TO_CHAR(ADD_MONTHS(TO_DATE(\\''201403\\'', \\''YYYYMM\\''), 1), \\''YYYYMM\\'') \r\n            and a1.finance_cp = \\''00002\\''\r\n        ), 0) AS  KEB_CNT\r\n        \r\n          	 , NVL( (select\r\n          count (a1.complex_cd)\r\n          from \r\n          zem_applicant a1,ZET_MAINT_FEE_REQ r1,ZEM_COMPLEX_INFO  C1\r\n          where r1.charge_ym = \\''201403\\''\r\n          and a1.complex_cd = r1.complex_cd(+)\r\n          and a1.bd_cd = r1.bd_cd(+)\r\n          and a1.house_no = r1.house_no(+)\r\n          AND A1.DTAIL_DIV  = R1.DTAIL_DIV(+)\r\n          and a1.complex_cd = c.complex_cd\r\n          and c1.complex_cd = a1.complex_cd\r\n          and C1.reg_proc_stat = \\''RG003\\''\r\n           AND NVL(A1.CLS_DT, \\''29001231\\'') > \\''201204\\'' || \\''31\\''\r\n           AND A1.DTAIL_DIV  = R1.DTAIL_DIV(+)\r\n          and A1.SETTL_YM <= TO_CHAR(ADD_MONTHS(TO_DATE(\\''201403\\'', \\''YYYYMM\\''), 1), \\''YYYYMM\\'') \r\n            and a1.finance_cp = \\''00003\\''\r\n        ), 0) AS  HYUNDAI_CNT\r\n         , NVL( (select\r\n          count (a1.complex_cd)\r\n          from \r\n          zem_applicant a1,ZET_MAINT_FEE_REQ r1,ZEM_COMPLEX_INFO  C1\r\n          where r1.charge_ym = \\''201403\\''\r\n          and a1.complex_cd = r1.complex_cd(+)\r\n          and a1.bd_cd = r1.bd_cd(+)\r\n          and a1.house_no = r1.house_no(+)\r\n          AND A1.DTAIL_DIV  = R1.DTAIL_DIV(+)\r\n          and a1.complex_cd = c.complex_cd\r\n          and c1.complex_cd = a1.complex_cd\r\n          and C1.reg_proc_stat = \\''RG003\\''\r\n           AND NVL(A1.CLS_DT, \\''29001231\\'') > \\''201204\\'' || \\''31\\''\r\n           AND A1.DTAIL_DIV  = R1.DTAIL_DIV(+)\r\n          and A1.SETTL_YM <= TO_CHAR(ADD_MONTHS(TO_DATE(\\''201403\\'', \\''YYYYMM\\''), 1), \\''YYYYMM\\'') \r\n            and a1.finance_cp = \\''00004\\''\r\n        ), 0) AS  KB_CNT\r\n         , NVL( (select\r\n          count (a1.complex_cd)\r\n          from \r\n          zem_applicant a1,ZET_MAINT_FEE_REQ r1,ZEM_COMPLEX_INFO  C1\r\n          where r1.charge_ym = \\''201403\\''\r\n          and a1.complex_cd = r1.complex_cd(+)\r\n          and a1.bd_cd = r1.bd_cd(+)\r\n          and a1.house_no = r1.house_no(+)\r\n          AND A1.DTAIL_DIV  = R1.DTAIL_DIV(+)\r\n          and a1.complex_cd = c.complex_cd\r\n          and c1.complex_cd = a1.complex_cd\r\n          and C1.reg_proc_stat = \\''RG003\\''\r\n           AND NVL(A1.CLS_DT, \\''29001231\\'') > \\''201204\\'' || \\''31\\''\r\n           AND A1.DTAIL_DIV  = R1.DTAIL_DIV(+)\r\n          and A1.SETTL_YM <= TO_CHAR(ADD_MONTHS(TO_DATE(\\''201403\\'', \\''YYYYMM\\''), 1), \\''YYYYMM\\'') \r\n            and a1.finance_cp = \\''00005\\''\r\n        ), 0) AS  HANA_CNT\r\n         , NVL( (select\r\n          count (a1.complex_cd)\r\n          from \r\n          zem_applicant a1,ZET_MAINT_FEE_REQ r1,ZEM_COMPLEX_INFO  C1\r\n          where r1.charge_ym = \\''201403\\''\r\n          and a1.complex_cd = r1.complex_cd(+)\r\n          and a1.bd_cd = r1.bd_cd(+)\r\n          and a1.house_no = r1.house_no(+)\r\n          AND A1.DTAIL_DIV  = R1.DTAIL_DIV(+)\r\n          and a1.complex_cd = c.complex_cd\r\n          and c1.complex_cd = a1.complex_cd\r\n          and C1.reg_proc_stat = \\''RG003\\''\r\n           AND NVL(A1.CLS_DT, \\''29001231\\'') > \\''201204\\'' || \\''31\\''\r\n           AND A1.DTAIL_DIV  = R1.DTAIL_DIV(+)\r\n          and A1.SETTL_YM <= TO_CHAR(ADD_MONTHS(TO_DATE(\\''201403\\'', \\''YYYYMM\\''), 1), \\''YYYYMM\\'') \r\n            and a1.finance_cp = \\''00006\\''\r\n        ), 0) AS  AMX_CNT\r\n	      ,\\''201403\\'' AS CHARGE_YM\r\n		  ,TO_CHAR(ADD_MONTHS(TO_DATE(\\''201403\\'', \\''YYYYMM\\''), 1), \\''YYYYMM\\'') || decode(length(C.DUE_DT),1,\\''0\\''||C.DUE_DT,C.DUE_DT) AS DUE_DT\r\n	      ,C.TELNO\r\n	      ,I.ITCP_NM \r\n	      ,GROUPING(C.COMPLEX_CD) || GROUPING(C.COMPLEX_NM) || GROUPING(I.ITCP_NM) AS GRP\r\n	  FROM ZEM_COMPLEX_INFO  C\r\n	      ,ZEM_APPLICANT     A\r\n	      ,ZET_MAINT_FEE_REQ R\r\n	       ,ZEM_IT_COMPANY I \r\n	 WHERE (1=1)\r\n             and TO_CHAR(ADD_MONTHS(TO_DATE(\\''201403\\'', \\''YYYYMM\\''), 1), \\''YYYYMM\\'') || C.DUE_DT >= \\''20140401\\''\r\n             and TO_CHAR(ADD_MONTHS(TO_DATE(\\''201403\\'', \\''YYYYMM\\''), 1), \\''YYYYMM\\'') || C.DUE_DT <= \\''20140431\\''\r\n	   AND C.COMPLEX_CD = A.COMPLEX_CD\r\n	   AND A.SETTL_YM <= TO_CHAR(ADD_MONTHS(TO_DATE(\\''201403\\'', \\''YYYYMM\\''), 1), \\''YYYYMM\\'') \r\n	   AND NVL(A.CLS_DT, \\''29001231\\'') > \\''201403\\'' || \\''31\\'' \r\n	   AND A.COMPLEX_CD = R.COMPLEX_CD(+)\r\n	   AND A.BD_CD      = R.BD_CD(+)\r\n	   AND A.HOUSE_NO   = R.HOUSE_NO(+)\r\n	   AND   NVL(A.CLS_DT, \\''29991231\\'') > TO_CHAR(ADD_MONTHS(TO_DATE(\\''201403\\'', \\''YYYY-MM\\''), 1), \\''YYYYMM\\'') || \\''31\\''   \r\n	   AND A.DTAIL_DIV  = R.DTAIL_DIV(+)\r\n	   AND R.CHARGE_YM(+) = \\''201403\\''\r\n	   AND I.ITCP_CD = C.ITCP_CD\r\n	   AND C.reg_proc_stat = \\''RG003\\''\r\n	 GROUP BY ROLLUP(C.COMPLEX_CD, C.COMPLEX_NM, C.DUE_DT, C.TELNO,I.ITCP_NM)\r\n	HAVING GROUPING(C.COMPLEX_CD) || GROUPING(C.COMPLEX_NM) || GROUPING(I.ITCP_NM) IN (\\''000\\'', \\''111\\'')\r\n	 ORDER BY C.COMPLEX_CD\r\n	 ', '', '20140520', '20141111', NULL),
