(54, 1, '', ' select /* IMS.nbilling.BuyerSellerMngReadMap.getSellerManageList */ \r\n             a.req_dt as \\"req_dt\\", a.pid as \\"pid\\", a.pid_nm as \\"pid_nm\\", a.co_nm as \\"co_nm\\", \r\n             a.co_no as \\"co_no\\", a.boss_nm as \\"boss_nm\\", a.buyer_co_no as \\"buyer_co_no\\",\r\n             a.mid as \\"mid\\", a.gid as \\"gid\\", a.aid as \\"aid\\", a.currency_cl as \\"currency_cl\\",   \r\n             decode(a.use_cl,\\''0\\'',\\''사용\\'',\\''1\\'',\\''중지\\'',\\''2\\'',\\''해지\\'', \\''\\'') as \\"use_cl\\",\r\n             case when a.co_status = \\''1\\'' then \\''휴업\\'' when a.co_status = \\''2\\'' then \\''폐업\\'' when a.co_status in (\\''3\\'',\\''4\\'',\\''5\\'',\\''6\\'') then \\''기타(위장)\\''  else \\''정상\\'' end \\"co_status\\",\r\n             a.req_cnt as \\"req_cnt\\",\r\n             a.status as \\"status\\",\r\n             a.totPageCnt as \\"totPageCnt\\"  \r\n    from (\r\n        select \r\n             a.req_dt , b.pid, b.pid_nm, a.co_nm, a.co_no, a.boss_nm, a.buyer_co_no,  \r\n             c.mid, c.gid, c.aid, c.currency_cl, c.use_cl, \r\n             decode(a.status, \\''02\\'', 0, (select count(1) from tb_seller_req_bill where co_no = a.co_no and pid=a.pid) ) as req_cnt,\r\n             a.status, (select status from close_info where buss_id = a.co_no) co_status,\r\n             count(1) over() as totPageCnt  \r\n        from tb_seller_master_bill a\r\n        left join tb_project_bill b on  a.pid = b.pid\r\n        left outer join tb_merchant c on a.mid = c.mid \r\n        where 1=1\r\n        <isNotNull prepend=\\"AND\\" property=\\"frDt\\"> a.req_dt between #frDt:VARCHAR# and #toDt:VARCHAR# </isNotNull>\r\n        <isNotEqual prepend=\\"AND\\" property=\\"selPid\\" compareValue=\\"all\\">  a.pid = #selPid:VARCHAR#</isNotEqual> \r\n        <isNotEmpty prepend=\\"AND\\" property=\\"pnm\\"> b.pid_nm like \\''%\\''|| #pnm:VARCHAR# || \\''%\\'' </isNotEmpty>\r\n        <isEqual prepend=\\"AND\\" property=\\"selID\\" compareValu