(73, 1, '쿼리문 끄적끄적', 'select * from zet_maint_fee_req\r\n	where charge_ym=\\''201402\\''\r\n		and complex_cd||bd_cd||house_no in(\r\n		select m.complex_cd||m.bd_cd||m.house_no as cbh\r\n		  from (\r\n		        select a.*\r\n		          from zem_applicant a\r\n		             , (select * --distinct complex_cd\r\n		                  from zem_complex_info\r\n		                 where due_dt in (\\''4\\'',\\''5\\'')\r\n		               ) b\r\n		         where a.complex_cd = b.complex_cd\r\n		           and a.finance_cp in (\\''00004\\'',\\''00002\\'')\r\n		           and a.st_type <> \\''3\\''\r\n		           and a.pay_start_dt < 20140205\r\n		       ) m        \r\n		)\r\n                    \r\n                    \r\n\r\n                    select * from zet_maint_fee_req a , zet_send_history b\r\n                    where a.complex_cd||a.bd_cd||a.house_no = b.crd_no\r\n                    and a.finance_cp = b.finance_cp \r\n                    and app_dt = \\''20140203\\''\r\n                    and a.charge_ym =\\''201402\\''\r\n                     \r\n                    \r\n                    select * from zet_send_history where app_dt=\\''20140203\\''\r\n                    \r\n                    select * from zem_applicant where complex_cd=\\''N0093\\'' and bd_cd=\\''0004\\'' and house_no=\\''0004\\''', '', '20140818', '20140819', NULL),
