(128, 1, '프로시져 확인방법', '-- 어떤 프로시져들이 존재하는지 확인 \r\nSQL> SELECT DISTINCT(name) \r\n     FROM USER_SOURCE \r\n     WHERE TYPE = \\''PROCEDURE\\'';\r\n \r\nNAME\r\n------------------------------\r\nFORCURSOR_TEST\r\nIMPLICIT_CURSOR\r\n \r\n\r\n-- 프로시져 소스의 확인\r\nSQL> SELECT text \r\n     FROM USER_SOURCE \r\n     WHERE name=\\''FORCURSOR_TEST\\'';\r\n\r\n\r\n\r\n', '프로시져 확인방법\r\n\r\n\r\n\r\nEXEC pd_pfms_crd_result(\\''\\'');\r\n\r\nEXEC PD_PFMS_OTHR_INFO_CRD_RESULT_1(\\''\\'');\r\nexec PD_PFMS_OTHR_INFO_CRD_RESULT_2(\\''\\'');\r\nexec PD_PFMS_OTHR_INFO_CRD_RESULT_3(\\''\\'');\r\nexec PD_PFMS_OTHER_BC_RECV_NEW(\\''\\'');\r\nexec PD_PFMS_OTHER_CRD_RESULT(\\''\\'');\r\n\r\nEXEC pd_pfms_recv_p(\\''\\'');\r\nEXEC pd_pfms_recv(\\''\\'');\r\nEXEC pd_pfms_crd_sum(\\''\\'');\r\n\r\nEXEC pd_pfms_cas_result(\\''\\'');\r\n\r\nEXEC PD_PFMS_CRD_SUM_HASH(\\''\\'');\r\nEXEC PD_PFMS_EDI_WORK(\\''\\'');\r\nEXEC PD_PFMS_CRD_SUM_HASH2(\\''\\'');\r\nEXEC PD_PFMS_CRD_SUM_HASH3(\\''\\'');\r\n\r\nEXEC PD_PFMS_CRD_SUM_OTHER(\\''\\'');\r\nEXEC pd_pfms_deb_result(\\''\\'');\r\nEXEC pd_pfms_deb_sum(\\''\\'');\r\n\r\nEXEC PD_PFMS_CRD_SUM_DIFF_ETC(\\''\\'');\r\nEXEC PD_PFMS_CRD_SUM_DIFF_KFC_NEW(\\''\\'');\r\nEXEC PD_PFMS_KFC_INFO_SYNC(\\''\\'');\r\n\r\nEXEC PD_PFMS_CRD_SUM_DIFF_OTHER(\\''\\'');', '20150102', '20150102', NULL),
(129, 1, '1. 나이스포스 신ASP(adm.nicepos.co.kr) 조회 화면 추가', 'select insp_dt , \r\n(select 매장명 from 매장명테이블 where str_cd = a.str_cd) 매장명 , \r\npos_cd , insp_seq ,  insp_tp , insp_nm , 중요도, insp_agent,\r\ninsp_object , insp_protocol , insp_result , 사유 , 대책 , regi_dt\r\nfrom POS_INSPECTION_LOG 	\r\nwhere 1 = 1\r\norder by insp_dt desc , insp_seq \r\n\r\n\r\n\r\nselect frc_cd , brnd_cd , str_cd , \r\ninsp_dt , \r\n(select 매장명 from 매장명테이블 where str_cd = a.str_cd) 매장명 , \r\npos_cd , insp_seq ,  insp_tp , insp_nm , 중요도, insp_agent,\r\ninsp_object , insp_protocol , insp_result , 사유 , 대책 , regi_dt\r\nfrom POS_INSPECTION_LOG 	\r\nwhere 1 = 1\r\norder by insp_dt desc , insp_seq \r\n\r\n\r\n\r\n만들어야할것. _grid 파일과 jsp 파일 그리고 struts_list.xml , tiles.xml 설정까지!!\r\n\r\n쿼리문 확인 요청 드려야함.\r\n\r\n', 'struts2 , mybatis 구성 \r\n\r\naction , dao , model , xml 로 구성되어있다 한개의 ap당\r\n\r\njsp/list/etc/pos_inspection_log_list.jsp 파일 추가한후에\r\n\r\n넘겨주신 테이블구조서 및 UI 확인후 프로그램 해야함.\r\n\r\n\r\n리스팅 없이 기존 그리드 사용해서 개발해야함.\r\n\r\n특이사항으로는 디비 없이 개발을 해야하기때문에 테스트가 불가능하다.(버그 발생 100%)\r\n\r\n', '20141217', '20141217', NULL),
(130, 5, '임계치 설정 관련 쿼리문 끄적거림.', 'select * from tb_code where col_nm = \\''off_cl\\''  and code1 in (select off_cl  from tb_mon_threshold)\r\n\r\n\r\n\r\nselect\r\n(select desc1 from tb_code where code_cl=\\''0039\\'' and code1 = to_char(to_number(a.off_cl-1))  ) off_cl_nm  ,  off_cl,\r\n          (select desc1 from tb_code where code_cl = \\''0022\\'' and code1 =a.svc_cd) svc_cd_nm , svc_cd,\r\n          (  select desc1 from tb_code where code_cl=\\''0083\\'' and code1 = a. fn_cd)  fn_cd_nm , fn_cd ,\r\n          start_tm , end_tm  , status , to_char(mon_cycle) ,to_char(suc_per) , to_char(threshold_cnt)   \r\n from tb_mon_threshold a\r\norder by  svc_cd  ,fn_cd , to_number(start_tm)  \r\n \r\nselect fn_cd from tb_mon_threshold\r\n\r\nselect * from tb_code where code_cl=\\''0083\\''   \r\n\r\n \r\n \r\n \r\n \r\n \r\n select * from tb_code\r\n where desc1 like \\''%평일%\\''\r\n \r\n \r\n select * from tb_code where col_nm=\\''off_cl\\''  --\r\n \r\nselect * from tb_code where code_cl =\\''0039\\'' \r\n \r\n  select * from tb_code where col_nm=\\''svc_cd\\''   -- 지불수단\r\n  \r\n  select * from tb_code where code_cl = \\''0022\\''  \r\n  \r\n  \r\n  \r\n\r\n\r\n  select * from tb_code where col_nm=\\''fn_cd\\''', '', '20141219', '20141219', NULL),
